import React, { Component } from 'react'
import { register } from './UserFunction'
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import { RadioGroup, RadioButton } from 'react-radio-buttons';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import { Link } from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import VpnKeyIcon from '@material-ui/icons/VpnKey';

import Typography from '@material-ui/core/Typography';

import Container from '@material-ui/core/Container';
class Register extends Component {
  constructor() {
    super()
    this.state = {
      username: '',
      password: '',
      dia_chi: '',
      email: '',
      sdt: '',
      ngay_sinh: new Date(),
      gioi_tinh: '',
      errors: '',
    }
    this.onChange = this.onChange.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }

  handleChange = date => {
    this.setState({
      ngay_sinh: date
    });
  };
  onChange_radio = value => {
    this.setState({ gioi_tinh: value })
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }
  onSubmit(e) {
    e.preventDefault()

    const newUser = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      email: this.state.email,
      password: this.state.password
    }


    // register(newUser).then(res => {
    //   this.props.history.push(`/login`)
    // })
  }

  render() {
    return (
      <div style={{
        backgroundImage: `url("https://mdbootstrap.com/img/Photos/Horizontal/Nature/full%20page/img(11).jpg")`, backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover", width: "100%", height: "100%"
      }} >
        <div>
          <br />

          <Container component="main" maxWidth="xs" >
            <CssBaseline />
            <div style={{ marginTop: "5px", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <Avatar style={{ margin: "1px", backgroundColor: "red" }}

              >
                <VpnKeyIcon />
              </Avatar>
              <Typography component="h1" variant="h5">
                Đăng ký
       </Typography>
              <form style={{ marginLeft: "-100px", width: '150%', marginTop: "10px", borderRadius: 5 }} noValidate onSubmit={this.onSubmit}>
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  type="text"
                  placeholder="Tài khoản"
                  name="username"
                  autoComplete="username"

                  autoFocus
                  value={this.state.username}
                  onChange={this.onChange}
                />
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  type="password"
                  placeholder="Mật khẩu"
                  name="password"
                  autoComplete="password"

                  autoFocus
                  value={this.state.password}
                  onChange={this.onChange}
                />
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  id="email"
                  placeholder="Địa chỉ email"
                  name="email"
                  autoComplete="email"
                  autoFocus
                  value={this.state.email}
                  onChange={this.onChange}
                />
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  name="dia_chi"
                  placeholder="Địa chỉ"
                  type="text"
                  id="dia_chi"
                  value={this.state.dia_chi}
                  onChange={this.onChange}
                />
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  name="sdt"
                  placeholder="Số điện thoại"
                  type="text"
                  id="sdt"
                  value={this.state.sdt}
                  onChange={this.onChange}
                />
                <div style={{ display: 'inline', marginRight: '20px' }}>Ngày sinh: </div>
                <DatePicker
                  selected={this.state.ngay_sinh}
                  onChange={this.handleChange}
                  dateFormat="dd/MM/yyyy" />
                  <div>Ngày sinh: </div>
                <RadioGroup onChange={this.onChange_radio} horizontal>
                  <RadioButton style={{width:"20px"}} value="Nam">Nam</RadioButton>
                  <RadioButton value="Nữ">Nữ</RadioButton>
                </RadioGroup>

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  style={{ margin: "20px 0 20px" }}
                >
                  Đăng ký
         </Button>
                <Grid container>

                  <Grid item>
                    <Link to="/Login" variant="body2">
                      {"Bạn đã có tài khoản? Đăng Nhập"}
                    </Link>
                  </Grid>
                </Grid>
              </form>
            </div>

          </Container>
          <div style={{ height: "225px" }}>

          </div>
        </div>

      </div>

    )
  }
}

export default Register
