import React, { Component } from 'react';
import './createGroup.css';
import './createGroupJS.js';
export default class createGroup extends Component {
  constructor(props){
    super(props);
    this.state={
        nameRoom:'',
        passRoom_1:'',
        passRoom_2:''
    }
  }
  isChange =(event) =>{
    var name = event.target.name ;
    var value=event.target.value ;
    console.log(name);
    console.log(value);
    this.setState({
      [name]:value
    });

  }
  handleOnClick =() =>{
    console.log(JSON.stringify(this.state));
  }
     render() {

          return (
               <form>
        <label>
          <p className="label-txt">ENTER YOUR ROOM</p>
          <input onChange={(event)=> this.isChange(event)} type="text" className="input" name="nameRoom" />
          <div className="line-box">
            <div className="line" />
          </div>
        </label>
        <label>
          <p className="label-txt">ENTER YOUR PASSWORD</p>
          <input onChange={(event)=> this.isChange(event)} type="text" className="input" name="passRoom_1" />
          <div className="line-box">
            <div className="line" />
          </div>
        </label>
        <label>
          <p className="label-txt">ENTER YOUR PASSWORD</p>
          <input onChange={(event)=> this.isChange(event)} type="text" className="input" name="passRoom_2" />
          <div className="line-box">
            <div className="line" />
          </div>
        </label>
        <button type="reset" onClick={()=>this.handleOnClick()}>submit</button>
      </form>
          )
     }
}
